import React, { useContext, useState } from 'react';
import { CartContext } from '../CartContext';
import CartProduct from './CartProduct';
import logo from '../../assets/logo.png';
// import { faBars } from '@fortawesome/free-solid-svg-icons'
import './Navbar.css'
// import 'bootstrap/dist/css/bootstrap.min.css';

const NavbarComponent = () => {
    const cart = useContext(CartContext);
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => {
        if(show === true){
            setShow(false);
        } else {
            setShow(true);
        };
    };

    const checkout = async () => {
        await fetch('http://localhost:4000/checkout', {
            method: "POST",
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({items: cart.items})
        }).then((res) => {
            return res.json();
        }).then((res) => {
            if(res.url) {
                window.location.assign(res.url); // Forwarding user to Stripe
            }
        });
    }
    const serviceCount = cart.items.reduce((sum, product) => sum + product.quantity, 0);
    return (
        <>
          <div>
            <a href="/">
              <img src={logo} className="" alt="logo" />
            </a>
            <button onClick={handleShow}>Cart ({serviceCount} Items)</button>
          </div>
          {show && (
            <div>
            {serviceCount > 0 ? (
                <>
                <p>Items in your cart:</p>
                {cart.items.map((currentService, idx) => (
                    <CartProduct key={idx} id={currentService.id} quantity={currentService.quantity} />
                ))}
                <h1>Total: {cart.getTotalCost().toFixed(2)}</h1>
                <button variant="success" onClick={checkout}>
                    Checkout!
                </button>
                </>
            ) : (
                <h1 className='modal_body'>There are no items in your cart.</h1>
            )}
            </div>
          )}
        </>
      );      
}

export default NavbarComponent;
