import React, { useContext } from 'react';
import { Card, Button, Form, Row, Col, Image } from 'react-bootstrap';
import { CartContext } from '../CartContext';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';


const ServiceCard = (props) => {
  const service = props.service;
  const cart = useContext(CartContext);
  const serviceQuantity = cart.getServiceQuantity(service.id);

  const addOne = () => {
    console.log(cart);
    cart.addOneToCart(service.id);
    toast('Added item to cart');
  };

  return (
    <div>
      <h3>{service.title}</h3>
      <p>${service.price} + GST</p>
      <div>
        <img src={service.img_url} alt={service.title} />
      </div>
      {serviceQuantity > 0 ? (
        <>
          <div>
            <label>In Cart: {serviceQuantity}</label>
            <button onClick={addOne}>+</button>
            <button onClick={() => cart.removeOneFromCart(service.id)}>-</button>
            {serviceQuantity > 0 && (
              <>
                <button onClick={() => cart.deleteFromCart(service.id)} className='my-2' variant='danger'>Remove</button>
              </>
            )}
          </div>
        </>
      ) : (
        <>
          <button onClick={addOne}>Add To Cart</button>
        </>
      )}
    </div>
  );
};

export default ServiceCard;
