import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import React, {useState, useEffect} from 'react';
import Home from './components/Home/Home';
import About from './components/About/About';
import Services from './components/Services/Services';
import {
  BrowserRouter as Router,
  Routes,
  Route
} from "react-router-dom";
import Contact from './components/Contact/Contact';
import Nav from './components/Nav';
import FooterNav from './components/FooterNav';
import Footer from './components/Footer';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faArrowUp } from '@fortawesome/free-solid-svg-icons'
import Store from './components/Store/pages/Store';
import Success from './components/Store/pages/Success'
import Cancel from './components/Store/pages/Cancel'

function App() {
  const [showButton, setShowButton] = useState(false);

    useEffect(() => {
      window.addEventListener("scroll", () => {
        if (window.pageYOffset > 40) {
          setShowButton(true);
        } else {
          setShowButton(false);
        }
      });
    }, []);

    const scrollToTop = () => {
      window.scrollTo({
        top: 0,
        behavior: 'smooth'
      });
    };

  return (
    <Router>
      {showButton && (
        <button className='footer__btn' onClick={scrollToTop}>
          <FontAwesomeIcon icon={faArrowUp} />
        </button>
      )}
      {window.URL.toString().endsWith('/store') ? 
      (<Nav />)
      :
        (<Nav />)
      }
      <Routes>
        <Route path='/' index element={<Home />}>
        </Route>
        <Route path='services' element={<Services />}>
        </Route>
        <Route path='services/store' element={<Store />}>
        </Route>
        <Route path='services/success' element={<Success />} />
            <Route path='services/cancel' element={<Cancel />} />
        <Route path='about' element={<About />}>
        </Route>
        <Route path='contact' element={<Contact />}>
        </Route>
      </Routes>
      <FooterNav />
      <Footer />
    </Router>
  );
}

export default App;
